// OrganDetail.js
import React from 'react';

const OrganDetail = ({ selectedOrgan }) => {
  return (
    <div>
      <h2>Organ Detail</h2>
      {selectedOrgan ? (
        <div>
          <h3>{selectedOrgan.name}</h3>
          <p>{selectedOrgan.description}</p>
          <img src={selectedOrgan.imageUrl} alt={selectedOrgan.name} />
        </div>
      ) : (
        <p>Please select an organ</p>
      )}
    </div>
  );
};

export default OrganDetail;
